import logo from "./logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "./components/Header";
import MovieList from "./components/MovieList";
import React, { Component } from "react";
import { Redirect, Router, Switch } from "react-router-dom";
import MovieDetail from "./components/MovieDetail";
import MovieContainer from "./components/MovieContainer";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      movies: [],
      showDetail: false,
      movie: "",
    };
  }
  componentDidMount() {
    console.log("hii");
    fetch("http://127.0.0.1:8000/api/movies/", {
      method: "GET",
      headers: {
        // "Content-Type": "application/json",
        // 'Authorization': `Token ${this.props.token}`
      },
      //   body: JSON.stringify({ stars: stars + 1 }),
    })
      .then((resp) => resp.json())
      .then((resp) => this.setState({ movies: resp }))
      .catch((error) => console.log(error));
  }
  movieDetail = (id) => {
    console.log("hello");
    this.setState({
      detail: true,
    });
    fetch(`http://127.0.0.1:8000/api/movies/${id}`, {
      method: "GET",
      headers: {
        // "Content-Type": "application/json",
        // 'Authorization': `Token ${this.props.token}`
      },
      //   body: JSON.stringify({ stars: stars + 1 }),
    })
      .then((resp) => resp.json())
      .then((resp) => this.setState({ movie: resp }))
      .catch((error) => console.log(error));
    console.log({ movie });
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Header />
          <h1>Movies</h1>
          {this.state.movies.map((movie) => (
            <MovieContainer movieDetail={this.movieDetail} movie={movie} />
          ))}
          {this.state.detail ? <MovieDetail movie={this.state.movie} /> : ""}
        </header>
      </div>
    );
  }
}

export default App;
